import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const banners = [
  { id: 1, title: "Smart alış-verişə hazır ol!", subtitle: "GEEPAS MƏHSULLARI", price: "0 AZN", img: "data:image/jpeg;base64," },
  { id: 2, title: "Endirimlərdən yararlanın!", subtitle: "Yeni məhsullar", price: "20%", img: "data:image/jpeg;base64," },
  { id: 3, title: "Keyfiyyətli məişət texnikası", subtitle: "GEEPAS ilə", price: "50%", img: "data:image/jpeg;base64," }
];

const BannerCarousel = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
    adaptiveHeight: true,
  };

  return (
    <div className="w-full max-w-5xl mx-auto px-4">
      <Slider {...settings}>
        {banners.map((banner) => (
          <div key={banner.id} className="flex flex-col items-center bg-gray-200 p-8 text-center rounded-lg shadow-md">
            <h2 className="text-2xl font-bold">{banner.title}</h2>
            <p className="text-xl mt-2">{banner.subtitle}</p>
            <div className="text-4xl font-bold my-4">{banner.price} <span className="text-lg">aylıq</span></div>
            <img src={banner.img} alt={banner.subtitle} className="mx-auto  w-full max-w-xs" />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default BannerCarousel;